import React from "react";
import "../../css/buttonsStyle/buttonFour.css";
import { useState } from "react";

const ButtonFour = () => {
  const [classDivOne, setClassDivOne] = useState("bFourSprite-sun");
  const [classDivTwo, setClassDivTwo] = useState("bFourSprite-sun-after");
  const [classDivOne2, setClassDivOne2] = useState("iconMiddleCircle-sun");
  const [classDivTwo2, setClassDivTwo2] = useState(
    "iconMiddleCircle-sun-after"
  );
  const [classDivThree, setClassDivThree] = useState("bFourSprite-moon");
  const [classDivFour, setClassDivFour] = useState("bFourSprite-moon-after");

  const handleClickDiv = () => {
    setClassDivOne(
      classDivOne === "bFourSprite-sun"
        ? "bFourSprite-sun-after"
        : "bFourSprite-sun"
    );
    setClassDivTwo(
      classDivTwo === "bFourSprite-moon"
        ? "bFourSprite-moon-after"
        : "bFourSprite-moon"
    );
  };

  return (
    <div className="bFourFlexContainer">
      <div className={classDivOne} onClick={handleClickDiv}>
        <div className="iconMiddleCircle-sun"></div>
      </div>
      <div className="bFourSprite-moon">
        <div className="iconMiddleCircle-moon"></div>
      </div>
    </div>
  );
};

export default ButtonFour;
